mapper.insert:
INSERT INTO sys_dept (
    parent_id, ancestors, dept_name, order_num, leader, leader_user_id,
    phone, email, status, del_flag, dept_type, level,
    create_by, create_time, remark
) VALUES (
    #{parentId}, #{ancestors}, #{deptName}, #{orderNum}, #{leader}, #{leaderUserId},
    #{phone}, #{email}, #{status}, #{delFlag}, #{deptType}, #{level},
    #{createBy}, #{createTime}, #{remark}
)

mapper.update:
UPDATE sys_dept
parent_id = #{parentId}, ancestors = #{ancestors}, dept_name = #{deptName}, order_num = #{orderNum}, leader = #{leader}, leader_user_id = #{leaderUserId}, phone = #{phone}, email = #{email}, status = #{status}, dept_type = #{deptType}, level = #{level}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
WHERE dept_id = #{deptId}

UPDATE sys_dept 
SET order_num = #{orderNum}, update_time = sysdate()
WHERE dept_id = #{deptId}

mapper.select:
SELECT COUNT(1) FROM sys_dept
WHERE dept_name = #{deptName} AND parent_id = #{parentId} AND del_flag = '0'

    AND dept_id != #{deptId}

SELECT COUNT(1) FROM sys_user
WHERE status = '0' AND dept_id IN

    #{deptId}


WHERE del_flag = '0' AND status = '0'
AND dept_type = #{deptType}

    AND level = #{level}

ORDER BY order_num


WHERE del_flag = '0' AND status = '0'
AND leader_user_id = #{leaderUserId}
ORDER BY order_num

WHERE del_flag = '0' AND dept_id IN
#{deptId}
ORDER BY order_num

SELECT dept_type, COUNT(*) as count FROM sys_dept WHERE del_flag = '0' AND dept_id IN
#{deptId}
GROUP BY dept_type

SELECT level, COUNT(*) as count FROM sys_dept WHERE del_flag = '0' AND dept_type = '2' AND dept_id IN
#{deptId}
GROUP BY level

SELECT CASE WHEN dept_type = '3' THEN 'buyer_main' WHEN dept_type = '4' THEN 'buyer_sub' ELSE 'other' END as account_type, COUNT(*) as count FROM sys_dept WHERE del_flag = '0' AND dept_type IN ('3', '4') AND dept_id IN
#{deptId}
GROUP BY CASE WHEN dept_type = '3' THEN 'buyer_main' WHEN dept_type = '4' THEN 'buyer_sub' ELSE 'other' END

SELECT d.dept_id, d.dept_name, d.leader, d.create_time, d.status, ( SELECT COUNT(*) FROM sys_dept d2 WHERE d2.parent_id = d.dept_id AND d2.dept_type = '4' AND d2.del_flag = '0' ) as sub_account_count FROM sys_dept d WHERE d.del_flag = '0' AND d.dept_type = '3' AND d.dept_id IN
#{deptId}
ORDER BY d.create_time
