mapper.select:
SELECT * FROM sms_history
WHERE task_id = #{taskId}
ORDER BY sent_at DESC

mapper.update:
UPDATE sms_history
SET `read` = #{read}
WHERE task_id = #{taskId}
  AND target_number = #{targetNumber}

mapper.select:
SELECT task_id
FROM sms_history
WHERE message_from = #{messageTo}
  AND message_to = #{messageFrom}
  AND target_number = #{messageFrom}
ORDER BY sent_at DESC
LIMIT 1
