mapper.select:
SELECT id, user_id, instance_id, receiver_numbers, message_contents, sent_count, reply_count, total_count, status, created_at
FROM sms_tasks
WHERE id = #{id}

mapper.update:
UPDATE sms_tasks
SET status = #{status}
WHERE id = #{id}

UPDATE sms_tasks
SET sent_count = sent_count + #{delta}
WHERE id = #{id}

UPDATE sms_tasks
SET reply_count = reply_count + #{delta}
WHERE id = #{id}

UPDATE sms_tasks
SET total_count = total_count + #{delta}
WHERE id = #{id}
